<?php $executionStartTime = microtime(true); include("config.php"); $conn = new mysqli($cd_host, $cd_user, $cd_password, $cd_dbname, $cd_port, $cd_socket); if (mysqli_connect_errno()) { $output['status']['code'] = "300"; $output['status']['name'] = "failure"; $output['status']['description'] = "database unavailable"; $output['status']['returnedIn'] = (microtime(true) - $executionStartTime) / 1000 . " ms"; $output['data'] = []; mysqli_close($conn); header('Content-Type: application/json; charset=UTF-8', true, '300'); echo json_encode($output); exit; }	$query = 'SELECT i.id, i.name, i.stock, l.name as loc, l.id as locID FROM inventory i RIGHT JOIN locations l ON (l.id = i.locationID) ORDER BY i.name'; $result = $conn->query($query); if (!$result) { $output['status']['code'] = "400"; $output['status']['name'] = "executed"; $output['status']['description'] = "query failed";	$output['data'] = []; mysqli_close($conn); header('Content-Type: application/json; charset=UTF-8', true, '400'); echo json_encode($output); exit; } $data = []; while ($row = mysqli_fetch_assoc($result)) { array_push($data, $row); } $output['status']['code'] = "200"; $output['status']['name'] = "ok"; $output['status']['description'] = "success"; $output['status']['returnedIn'] = (microtime(true) - $executionStartTime) / 1000 . " ms"; $output['data'] = $data; mysqli_close($conn); header('Content-Type: application/json; charset=UTF-8', true, '200'); echo json_encode($output); ?>
